mapper.select:
select 

from tsp_service_in
where system_code = #{systemCode,jdbcType=VARCHAR}
  and sub_system_code = #{subSystemCode,jdbcType=VARCHAR}
  and out_service_code = #{outServiceCode,jdbcType=VARCHAR}

mapper.delete:
delete from tsp_service_in
where system_code = #{systemCode,jdbcType=VARCHAR}
  and sub_system_code = #{subSystemCode,jdbcType=VARCHAR}
  and out_service_code = #{outServiceCode,jdbcType=VARCHAR}
